mapper.select:
select 

  ,
  ,o.id as orgid,o.name as orgname,sex.name as sexname,acc.username,
  r.role_name as rolename
  from ecuser t
  left join user_org_job uoj on t.account_id=uoj.user_id
  left join organization o on uoj.org_id=o.id
  left join dic_sex sex on t.xbm=sex.id
  left join role r on t.roleid=r.id
  left join account acc on t.account_id=acc.id


  		and t.roleid=#{ecuser.roleid,jdbcType=BIGINT}


  		and (o.parent_id = #{parentorgid,jdbcType=BIGINT} or o.id = #{parentorgid,jdbcType=BIGINT})


  		and o.org_level = #{queryOrgEntity.orgLevel,jdbcType=INTEGER}


  		and o.provinceid = #{queryOrgEntity.provinceid,jdbcType=VARCHAR}


  		and o.cityid = #{queryOrgEntity.cityid,jdbcType=VARCHAR}


  		and o.countyid = #{queryOrgEntity.countyid,jdbcType=VARCHAR}


  		and o.townid = #{queryOrgEntity.countyid,jdbcType=VARCHAR}


  		and o.name like concat('%',#{queryOrgEntity.name,jdbcType=VARCHAR},'%')

select 

