mapper.insert:

insert into coach_record (id, username, data, 
  teacherid, consultationModeId, consultationTypeId, 
  sfzjh, xh, objtype, schoolid, 
  bjid, njid, xbm, describes
  )
values (#{id,jdbcType=BIGINT}, #{username,jdbcType=VARCHAR}, #{data,jdbcType=DATE}, 
  #{teacherid,jdbcType=BIGINT}, #{consultationmodeid,jdbcType=BIGINT}, #{consultationtypeid,jdbcType=BIGINT}, 
  #{sfzjh,jdbcType=VARCHAR}, #{xh,jdbcType=VARCHAR}, #{objtype,jdbcType=CHAR}, #{schoolid,jdbcType=BIGINT}, 
  #{bjid,jdbcType=BIGINT}, #{njid,jdbcType=TINYINT}, #{xbm,jdbcType=BIT}, #{describes,jdbcType=LONGVARCHAR}
  )

mapper.select:
   		select
			*
		from coach_record


				and username like "%"#{record.username}"%"


				and teacherid = #{record.teacherid,jdbcType=BIGINT}


				and consultationmodeid = #{record.consultationmodeid,jdbcType=INTEGER}


				and consultationtypeid = #{record.consultationtypeid,jdbcType=INTEGER}


				and objtype = #{record.objtype,jdbcType=CHAR}


				and schoolid = #{record.schoolid,jdbcType=BIGINT}


				and sfzjh = #{record.sfzjh,jdbcType=VARCHAR}


				and data >   #{beginDate,jdbcType=DATE}


				and data <   #{endDate,jdbcType=DATE}
