mapper.select:
select

from coach_warning_student


		and name  like "%"#{warningInterveneS.name}"%" 


		and sex = #{warningInterveneS.sex,jdbcType=CHAR}


		and cardid = #{warningInterveneS.cardid,jdbcType=VARCHAR}


		and class_name = #{warningInterveneS.className,jdbcType=VARCHAR}


		and grade = #{warningInterveneS.grade,jdbcType=VARCHAR}


		and type = #{warningInterveneS.type,jdbcType=CHAR}


		and result = #{warningInterveneS.result,jdbcType=CHAR}



		and status = #{warningInterveneS.status,jdbcType=CHAR}


		and level = #{warningInterveneS.level,jdbcType=CHAR}




		and examresult_id =
		#{warningInterveneS.examresultId,jdbcType=BIGINT}


		and school_id =
		#{warningInterveneS.schoolId,jdbcType=BIGINT}


		and groupid =
		#{warningInterveneS.groupid,jdbcType=INTEGER}







			and warning_time > #{beginDate,jdbcType=TIMESTAMP}


			and intervene_time > #{beginDate,jdbcType=TIMESTAMP}





			and warning_time < #{endDate,jdbcType=TIMESTAMP}


			and intervene_time < #{endDate,jdbcType=TIMESTAMP}




	order by warning_time desc


	order by intervene_time desc

select
