		#{warningInterveneS.groupid,jdbcType=INTEGER}







			and warning_time > #{beginDate,jdbcType=TIMESTAMP}


			and intervene_time > #{beginDate,jdbcType=TIMESTAMP}





			and warning_time < #{endDate,jdbcType=TIMESTAMP}


			and intervene_time < #{endDate,jdbcType=TIMESTAMP}




	order by warning_time desc


