mapper.select:

select 

from scale_norm
 where 1=1 

    and grade_id = #{gradeOrderId,jdbcType=TINYINT}


  	and gender = #{gender,jdbcType=TINYINT}


 	and scale_id = #{scaleId,jdbcType=INTEGER}


  	and w_id = #{wId,jdbcType=VARCHAR}

mapper.delete:

delete from scale_norm
where grade_id_id = #{gradeOrderId,jdbcType=TINYINT}
  and gender = #{gender,jdbcType=TINYINT}
  and scale_id = #{scaleId,jdbcType=INTEGER}
  and w_id = #{wId,jdbcType=VARCHAR}

mapper.update:

update scale_norm


    m = #{m,jdbcType=REAL},


    sd = #{sd,jdbcType=REAL},


where grade_id = #{gradeOrderId,jdbcType=TINYINT}
  and gender = #{gender,jdbcType=TINYINT}
  and scale_id = #{scaleId,jdbcType=INTEGER}
  and w_id = #{wId,jdbcType=VARCHAR}


update scale_norm
set m = #{m,jdbcType=REAL},
  sd = #{sd,jdbcType=REAL}
where grade_id = #{gradeOrderId,jdbcType=TINYINT}
  and gender = #{gender,jdbcType=TINYINT}
  and scale_id = #{scaleId,jdbcType=INTEGER}
  and w_id = #{wId,jdbcType=VARCHAR}
