mapper.insert:

insert into job_activity_summary (id, title, schoolyear, 
  term, activitycatalog, activitytype, 
  create_time, author, dep, 
  modify_time, content, ini_content
  )
values (#{id,jdbcType=BIGINT}, #{title,jdbcType=VARCHAR}, #{schoolyear,jdbcType=VARCHAR}, 
  #{term,jdbcType=CHAR}, #{activitycatalog,jdbcType=CHAR}, #{activitytype,jdbcType=CHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{author,jdbcType=VARCHAR}, #{dep,jdbcType=BIGINT}, 
  #{modifyTime,jdbcType=TIMESTAMP}, #{content,jdbcType=LONGVARCHAR}, #{iniContent,jdbcType=LONGVARCHAR}
  )

mapper.select:
	select t.id, title, schoolyear, x.name as term, c.name as activitycatalog, 
	tp.name as activitytype, create_time, author, 
  dep, modify_time from job_activity_summary t
	left join dic_xq x on t.term = x.id
	left join dic_job_activity_catalog c on t.activitycatalog = c.id
	left join dic_job_activity_type tp on t.activitytype = tp.id


			and schoolyear =#{entity.schoolyear,jdbcType=VARCHAR}


			and term = #{entity.term,jdbcType=CHAR}


			and activitycatalog = #{entity.activitycatalog,jdbcType=CHAR}


			and activitytype = #{entity.activitytype,jdbcType=CHAR}


			and title like concat('%',#{entity.title,jdbcType=VARCHAR},'%') 


			and create_time >= #{entity.querystarttime,jdbcType=DATE}


			and create_time <= #{entity.queryendtime,jdbcType=DATE}


	    order by create_time desc

select distinct(schoolyear) from job_activity_summary 


			dep = #{depid,jdbcType=BIGINT}

		and schoolyear is not null
