mapper.select:
  	 select * from school s where orgid in (select id from organization where org_type='2' and org_level = 6 

			and countyid in 

		 	 #{item}


		)

  	 select * from school s where orgid in (select id from organization where org_type='2' and org_level = 6 

			and cityid in 

		 	 #{item}


		)

  	 select * from school s where orgid in (select id from organization where org_type='2' and org_level = 6 

			and townid in 

		 	 #{item}


		 )

select ,
,bl.name as bxlxname,xx.name as xxbbname,cc.name as xxdwccname
from school s
left join organization o on s.orgid=o.id
left join dic_school_bxlx bl on bl.id=s.xxbxlxm
left join dic_school_xxbb xx on xx.id = s.xxbbm
left join dic_school_xxdwcc cc on cc.id=s.xxdwcc


		and xxbxlxm=#{school.xxbxlxm,jdbcType=CHAR}


		and xxbbm=#{school.xxbbm,jdbcType=CHAR}


		and xxdwcc=#{school.xxdwcc,jdbcType=CHAR}


		and xxmc like CONCAT('%',#{school.xxmc,jdbcType=VARCHAR},'%' ) 


		and o.parent_id=#{school.org.parentId,jdbcType=BIGINT}
