mapper.select:
   select

		from coach_online_email


				and toid = #{email.toid,jdbcType=BIGINT}


				and fromid = #{email.fromid,jdbcType=BIGINT}


				and is_read = #{email.isRead,jdbcType=BIGINT}


				and emailstatus = #{email.emailstatus,jdbcType=BIGINT}


				and is_replied = #{email.isReplied,jdbcType=BIGINT}


				and parent_id = #{email.parentId,jdbcType=BIGINT}


				and schoolid = #{email.schoolid,jdbcType=BIGINT}


				and title like "%"#{email.title}"%" 


				and send_date >   #{beginDate,jdbcType=TIMESTAMP}


				and send_date <   #{endDate,jdbcType=TIMESTAMP}


		order by send_date desc
