		and result = #{warningInterveneT.result,jdbcType=CHAR}



		and status = #{warningInterveneT.status,jdbcType=CHAR}


		and level = #{warningInterveneT.level,jdbcType=CHAR}




		and examresult_id =
		#{warningInterveneT.examresultId,jdbcType=BIGINT}


		and school_id =
		#{warningInterveneT.schoolId,jdbcType=BIGINT}







			and warning_time > #{beginDate,jdbcType=TIMESTAMP}


			and intervene_time > #{beginDate,jdbcType=TIMESTAMP}





			and warning_time < #{endDate,jdbcType=TIMESTAMP}


			and intervene_time < #{endDate,jdbcType=TIMESTAMP}




	order by warning_time desc


	order by intervene_time desc
