mapper.select:
select
,
,cat.name as activitycatalogname,tp.name as activitytypename, st.name as statename,a.realname as authorName,xq.name as termname from job_activityplan t left join dic_job_activity_catalog cat on t.activitycatalog=cat.id left join dic_job_activity_type tp on t.activitytype=tp.id left join dic_job_statenosend st on st.id = t.state left join account a on a.id = t.author left join dic_xq xq on xq.id=t.term where t.id = #{id,jdbcType=BIGINT}

	select 

  ,

  ,cat.name as activitycatalogname,tp.name as activitytypename,
  st.name as statename,a.realname as authorName,xq.name as termname
	 from job_activityplan t
	left join dic_job_activity_catalog cat on t.activitycatalog=cat.id
	left join dic_job_activity_type tp on t.activitytype=tp.id
	left join dic_job_statenosend st on st.id = t.state
	left join account a on a.id = t.author
	left join dic_xq xq on xq.id=t.term


			t.title like concat('%',#{params.title,jdbcType=LONGVARCHAR},'%')


			and t.state = #{params.state,jdbcType=BIGINT}


			and t.schoolyear = #{params.schoolyear,jdbcType=VARCHAR}


			and t.term = #{params.term,jdbcType=VARCHAR}


			and t.activitycatalog = #{params.activitycatalog,jdbcType=VARCHAR}


			and t.activitytype = #{params.activitytype,jdbcType=VARCHAR}


			and t.dep = #{params.dep,jdbcType=BIGINT}



      and (t.AUTHOR = #{params.author,jdbcType=BIGINT} or (t.AUTHOR != #{params.author,jdbcType=BIGINT} and (t.state=3 or t.state=2)))


	order by write_time desc

mapper.update:
	update job_activityplan set 
    state = #{record.state,jdbcType=CHAR}
  where id = #{record.id,jdbcType=BIGINT}
