    public static Integer getGradecodeByXdNj(Integer xd, Integer nj) {
        Calendar cal = Calendar.getInstance();
        int plusGrade = 0;
        // 升级月份为9月份
        if (cal.get(Calendar.MONTH) >= 9)
            plusGrade = 1;
        if (xd == XueDuan.prim_school.value()) {
            return cal.get(Calendar.YEAR) - nj + plusGrade;
        } else if (xd == XueDuan.j_school.value()) {
            return cal.get(Calendar.YEAR) - nj + plusGrade + 6;
        } else if (xd == XueDuan.s_school.value()) {
            return cal.get(Calendar.YEAR) - nj + plusGrade + 10;
        }
        return null;
    }
